import { Component } from "@angular/core";

@Component({
  selector: "app-post",
  templateUrl: "./post.component.html",
  styleUrls: ["./post.component.scss"],
})
export class PostComponent {
  @Input() title = '';
    @Input() text = '';
}
<main>
    <app-add-post (onPost)="addPost($event)"></app-add-post>
    <app-post [title]="post.title" [text]="post.text" [chartAmount]="chartAmount"></app-post>
</main>
