import {Component, DoCheck, OnInit} from '@angular/core';

@Component({
    selector: 'app-root',
    templateUrl: './app.component.html',
    styleUrls: ['./app.component.scss']
})
export class AppComponent implements OnInit, DoCheck {
    // post = {
    //     title: 'Gaming and Creating',
    //     text: 'NVIDIA GeForce RTX powers the world’s fastest GPUs and the ultimate platform for gamers and creators. Enjoy beautiful ray tracing, AI-powered DLSS, and much more in games and applications, on your desktop, laptop, in the cloud, or in your living room.'
    // }
    post = {title: '', text: ''}

    addPost(newPost: any) {
        this.post.title = newPost.title;
        this.post.text = newPost.text;
    }

    chartAmount = 0;

    ngOnInit() {
        this.chartAmount = this.post.text.length;
    }

    ngDoCheck() {
        this.chartAmount = this.post.text.length;
    }
}

//Gaming and Creating
