import { Component,} from '@angular/core';

@Component({
  selector: 'app-user',
  templateUrl: './user.component.html',
  styleUrls: ['./user.component.scss'],
})
export class UserComponent {
  @Input() name = ''
  @Input() status = ''
  @Input() url = ''
  userDate = new Date

}
<div class="wrapper">
    <div class="body">
        <div class="picture">
            <img src="" alt="">
        </div>
        <div class="user-data">
            <h2>[name]</h2>
            <div class="post-text">status</div>
        </div>
    </div>
    <button class="network">Оnline</button>
</div>
