import { Component, Input } from "@angular/core";
import { UsersGroupNameService } from "../service/users-group-name.service";
import { UsersService } from "../service/users.service";

@Component({
  selector: "app-user",
  templateUrl: "./user.component.html",
  styleUrls: ["./user.component.scss"],
})
export class UserComponent {
  @Input() name = "";
  @Input() status = "";
  @Input() img = "";
  @Input() userId = this.U // Додайте властивість userId

  newGroupName: string = "";
  newName: string = "";
  newStatus: string = "";

  constructor(
    public usersGroupNameService: UsersGroupNameService,
    public usersService: UsersService
  ) {}

  updateGroupName() {
    this.usersGroupNameService.groupName = this.newGroupName;
  }

  updateUserInfo() {
    this.usersService.updateUserDetails(
      this.userId,
      this.newName,
      this.newStatus
    );
  }
}
<div class="wrapper">
    <div class="body">
        <div class="picture">
            <img [src]="img" alt="">
        </div>
        <div class="user-data">
            <div class="user-data-item">
                <h2>{{name}}</h2>
                <div class="edit-body">
                    <input type="text" placeholder="Write new name"  [(ngModel)]="newName">
                    <button class="network">Edit</button>
                </div>
            </div>
            <div class="user-data-item">
                <div class="status-text">{{status}}</div>
                <div class="edit-body">
                    <input type="text" placeholder="Write new status" [(ngModel)]="newStatus">
                    <button class="network" (click)="updateUserInfo()">Edit</button>
                </div>
            </div>
        </div>
    </div>
<div class="group">
    <p><b>Group Name:</b><span > {{ usersGroupNameService.groupName }}</span></p>
</div>
</div>
