import { Component, Input } from "@angular/core";
import { UsersGroupNameService } from "../service/users-group-name.service";
import { UsersService } from "../service/users.service";
@Component({
  selector: "app-user",
  templateUrl: "./user.component.html",
  styleUrls: ["./user.component.scss"],
})
export class UserComponent {
  @Input() name = "";
  @Input() status = "";
  @Input() img = "";
  newGroupName: string = '';
  newName: string = ''; // Додайте поле для нового імені
  newStatus: string = ''; // Додайте поле для нового статусу
  constructor(public usersGroupNameService: UsersGroupNameService, user) {}

  updateGroupName() {
    this.usersGroupNameService.groupName = this.newGroupName;
  }
  updateUserInfo() {
    this.usersService.updateUserDetails(this.newName, this.newStatus);
  }
}
