import { Injectable } from "@angular/core";

@Injectable()
export class UsersService {
  usersInfo = [
    {
      name: "Oleksandr",
      status: "I'm learning angular",
      url: "https://i.pravatar.cc/150?img=12",
      id: 1,
    },
    {
      name: "Nikola",
      status: "Playing piano",
      url: "https://i.pravatar.cc/150?img=7",
      id: 2,
    },
    {
      name: "Bob",
      status: "Translations from Chinese",
      url: "https://i.pravatar.cc/150?img=11",
      id: 3,
    },
    {
      name: "Anna",
      status: "Pretty girl",
      url: "https://i.pravatar.cc/150?img=1",
      id: 4,
    },
  ];
  getUsers() {
    return this.usersInfo;
  }
  updateUserDetails(newName: string, newStatus: string) {
    if (userId >= 0 && userId < this.usersInfo.length) {
      this.usersInfo[userId].name = newName;
      this.usersInfo[userId].status = newStatus;
    }
  }
}
