import { Component, Input } from "@angular/core";
import { UsersGroupNameService } from "../service/users-group-name.service";

@Component({
  selector: "app-user",
  templateUrl: "./user.component.html",
  styleUrls: ["./user.component.scss"],
})
export class UserComponent {
  @Input() name = "";
  @Input() status = "";
  @Input() img = "";
  newGroupName: string = '';
  usersInfo = [];
  constructor(public usersGroupNameService: UsersGroupNameService,public usersService: UsersService) {}

  updateGroupName() {
    this.usersGroupNameService.groupName = this.newGroupName;
  }
}
