import {
  Component,
  ViewChild,
  ViewContainerRef,
  ComponentRef,
  ComponentFactoryResolver, // Додано імпорт
} from "@angular/core";
import { UsersService } from "./service/users.service";
import { PopUpComponent } from "./pop-up/pop-up.component";

@Component({
  selector: "app-root",
  templateUrl: "./app.component.html",
  styleUrls: ["./app.component.scss"],
  providers: [UsersService],
})
export class AppComponent {
  users: any[];

  @ViewChild("popUp", { read: ViewContainerRef })
  private viewRef!: ViewContainerRef;

  constructor(
    public usersService: UsersService,
    private componentFactoryResolver: ComponentFactoryResolver // Додано інжекцію ComponentFactoryResolver
  ) {
    this.users = usersService.usersInfo;
  }

  showPopUp() {
    // Отримання фабрики компонента та створення екземпляра
    const factory = this.componentFactoryResolver.resolveComponentFactory(PopUpComponent);
    this.viewRef.clear();
    const componentRef = factory.create(this.viewRef.parentInjector);
    this.viewRef.insert(componentRef.hostView);
    componentRef.instance.members = this.users.length;
  }
}
