import { Component, Input } from "@angular/core";
import { UsersService } from "../service/users.service";

@Component({
  selector: "app-user",
  templateUrl: "./user.component.html",
  styleUrls: ["./user.component.scss"],
})
export class UserComponent {
  @Input() name = "";
  @Input() status = "";
  @Input() img = "";
  newGroupName: string = '';
  newName: string = ''; 
  newStatus: string = ''; 

  constructor(public usersService: UsersService) {}

  updateGroupName() {
    this.usersService.groupName = this.newGroupName;
  }

  // Метод для оновлення імені та статусу користувача
  updateUserInfo() {
    // Викликайте метод updateUserDetails сервісу
    this.usersService.updateUserDetails(this.userId, this.newName, this.newStatus);
  }
}
